import Button from '../components/Button';
import '../css/to-do_style.css';
import React, { useEffect, useState } from "react";

function ToDo() {
  const [todos, setTodos] = useState([]);
  const [description, setDescription] = useState("");
  const [showModal, setShowModal] = useState(false);

  const getTodos = async () => {
    try {
      const response = await fetch("http://localhost:3001/tasks");  // Use full URL if proxy doesn't work
      if (!response.ok) throw new Error("Failed to fetch tasks");
      const jsonData = await response.json();
      setTodos(jsonData);
    } catch (err) {
      console.error("Error fetching tasks:", err.message);
    }
  };

  useEffect(() => {
    getTodos();
  }, []);

  const postTodos = async (e) => {
    e.preventDefault();
    try {
      if (!description.trim()) return alert("Task description cannot be empty!");
      const body = { description, status: "pending" };
      const response = await fetch("http://localhost:3001/tasks", {  
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (response.ok) {
        setDescription("");  // Clear input
        setShowModal(false);  // Close modal
        getTodos();  // Refresh tasks
      } else {
        console.error("Failed to add task");
      }
    } catch (err) {
      console.error("Error adding task:", err.message);
    }
  };

  const toggleStatus = async (todo) => {
    try {
      const newStatus = "Completed";
      const completionTime = new Date().toISOString();  // Store completion timestamp

      const response = await fetch(`http://localhost:3001/tasks/${todo.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: newStatus, completedAt: completionTime }),
      });

      if (response.ok) {
        getTodos();  
      } else {
        console.error("Failed to update status");
      }
    } catch (err) {
      console.error("Error updating status:", err.message);
    }
  };

  useEffect(() => {
    const deleteCompletedTasks = async () => {
      const now = new Date().getTime();

      for (const todo of todos) {
        if (todo.status === "Completed" && todo.completedAt) {
          const completedTime = new Date(todo.completedAt).getTime();
          if (now - completedTime >= 60000) {  // 60,000ms = 1 minute
            try {
              await fetch(`http://localhost:3001/tasks/${todo.id}`, {
                method: "DELETE",
              });
            } catch (err) {
              console.error("Error deleting task:", err.message);
            }
          }
        }
      }

      getTodos();  // Refresh the list
    };

    const interval = setInterval(deleteCompletedTasks, 10000); // Check every 10 sec
    return () => clearInterval(interval);
  }, [todos]);  // Runs when `todos` change


  return (
    <div className="To-Do">
      <div className='head'>
        <img src='/profile-img.png' className="profile-img" alt="profile-img" />
        <div>
          <h4>Monika Gamage</h4>
          <h6>@monicagamage</h6>
          <Button url="/" style={{ color: '#F4C27F' }}>Log Out</Button>
        </div>
      </div>

      <div className='to-do'>
        <h4>Tasks List</h4>
        <img 
          src='/plus-circle.png' 
          className="plus-img" 
          alt="Add Task" 
          onClick={() => setShowModal(true)} 
          style={{ cursor: "pointer" }}
        />

        <div className="task-container">
          {todos.map((todo) => (
            <div className="task-item" key={todo.id}>
              <input 
                type="checkbox" 
                checked={todo.status === 'Completed'}
                onChange={() => toggleStatus(todo)}
              />
              <span className={todo.status === 'Completed' ? "completed" : ""}>
                {todo.description}
              </span>
            </div>
          ))}
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h4>Add New Task</h4>
            <form onSubmit={postTodos}>
              <input
                type="text"
                className="input"
                placeholder="Task description..."
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">Add</button>
                <button type="button" className="close-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ToDo;
