import Button from '../components/Button';
import logo from '../to-do.webp';
import '../css/to-do_style.css';
import React, { useEffect, useState } from "react";

function ToDo() {
  const [todos, setTodos] = useState([]); // Initially an empty array []

  // Fetch the tasks from the server
  const getTodos = async () => {
    try {
      const response = await fetch("/tasks");
      const jsonData = await response.json();
      setTodos(jsonData);  // Setting the fetched tasks to state
    } catch (err) {
      console.error(err.message);
    }
  };

  // UseEffect hook to fetch tasks when the component is mounted
  useEffect(() => {
    getTodos();
  }, []);

  // Handle checkbox toggle
  const toggleStatus = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].status = updatedTodos[index].status === 'Completed' ? 'Pending' : 'Completed';
    setTodos(updatedTodos);
  };

  return (
    <div className="To-Do">
      <div className='head'>
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <h4>Monika Gamage</h4>
          <h6>@monicagamage</h6>
          <Button url="/" style={{ color: '#F4C27F' }}>Log Out</Button>
        </div>
      </div>

      <div className='to-do'>
        <h4>Tasks List</h4>
        <img src='/plus-circle.png' className="plus-img" alt="plus" />
        <table>
          <tbody>
            {todos.map((todo, index) => (
              <tr key={index}>
                <td>
                  <input 
                    type="checkbox" 
                    checked={todo.status === 'Completed'}
                    onChange={() => toggleStatus(index)}
                  />
                </td>
                <td>{todo.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ToDo;
