import { Link } from 'react-router-dom';
import Button from '../components/Button';
import logo from '../to-do.webp';
import '../css/input_fields_style.css'

const postUsers = async (e) => {
  e.preventDefault();
  try {
    // if (!description.trim()) return alert("Task description cannot be empty!");
    const body = { description, status: "pending" };
    const response = await fetch("http://localhost:3001/tasks", {  
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(body),
    });

    if (response.ok) {
      setDescription("");  // Clear input
      setShowModal(false);  // Close modal
      getTodos();  // Refresh tasks
    } else {
      console.error("Failed to add task");
    }
  } catch (err) {
    console.error("Error adding task:", err.message);
  }
};

function Registration() {
  return (
    <div className="Register">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>Get's things done with TODO</h2>
        <h5>Let's help you meet up your tasks</h5>
        <div className = 'input-fields'>
            <form>
                <input type = "text" name = "username" placeholder='Enter your full name'/>
                <br/>
                <input type = "email" name = "email" placeholder='Enter your email'/>
                <br/>
                <input type = "password" name = "password" placeholder='Enter password'/>
                <br/>
                <input type = "password" name = "confirm-password" placeholder='Comfirm password'/>
            </form>
        </div>

        <Button url = "/to-do">Register</Button>
        <h5>
          Already have an account ? <Link to="/login" className="sign-up-link">Sign In</Link>
          </h5>
    </div>
  );
}

export default Registration;
