import Button from '../components/Button';
import '../css/to-do_style.css';
import React, { useEffect, useState } from "react";

function ToDo() {
  const [todos, setTodos] = useState([]);
  const [description, setDescription] = useState("");
  const [showModal, setShowModal] = useState(false)

  // Fetch the tasks from the server
  const getTodos = async () => {
    try {
      const response = await fetch("/tasks");
      const jsonData = await response.json();
      setTodos(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getTodos();
  }, []);

  const postTodos = async(e) => {
    e.preventDefault();
    try{
      const body = { description };
      const response = await fetch("/tasks", {
        method: "POST",
        headers: { "Content-type": "application/json"},
          body: JSON.stringify(body)
      });
      if (response.ok) {
        setDescription("");  // Clear input field after adding
        getTodos(); // Refresh the tasks list
      }
    } catch (err) {
      console.log(err.message);
    }
  }

  // Toggle task status
  const toggleStatus = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].status = updatedTodos[index].status === 'Completed' ? 'Pending' : 'Completed';
    setTodos(updatedTodos);
  };

  return (
    <div className="To-Do">
      <div className='head'>
        <img src='/profile-img.png' className="profile-img" alt="profile-img" />
        <div>
          <h4>Monika Gamage</h4>
          <h6>@monicagamage</h6>
          <Button url="/" style={{ color: '#F4C27F'}}>Log Out</Button>
          </div>
      </div>

      <div className='to-do'>
        <h4>Tasks List</h4>
        <form onSubmit={postTodos} className="task-form">
          <input type="text" className="input" placeholder="Add a new task..." value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <button type="submit" className="add-btn">
            <img src='/plus-circle.png' className="plus-img" alt="Add Task" />
          </button>
        </form>

        <div className="task-container">
          {todos.map((todo, index) => (
            <div className="task-item" key={index}>
              <input 
                type="checkbox" 
                checked={todo.status === 'Completed'}
                onChange={() => toggleStatus(index)}
              />
              <span className={todo.status === 'Completed' ? "completed" : ""}>
                {todo.description}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ToDo;
