import Button from '../components/Button';
import '../css/to-do_style.css';
import React, { useEffect, useState } from "react";

function ToDo() {
  const [todos, setTodos] = useState([]);
  const [description, setDescription] = useState("");
  const [showModal, setShowModal] = useState(false);

  // Fetch tasks from the server
  const getTodos = async () => {
    try {
      const response = await fetch("/tasks");
      const jsonData = await response.json();
      setTodos(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getTodos();
  }, []);

  const postTodos = async (e) => {
    e.preventDefault();
    try {
      const body = { description, status: "pending" };
      const response = await fetch("/t", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (response.ok) {
        setDescription("");  // Clear input field
        setShowModal(false);  // Close modal
        await getTodos();  // Refresh the list AFTER successful request
      } else {
        console.error("Failed to add task");
      }
    } catch (err) {
      console.error(err.message);
    }
  };

  // Toggle task status
  const toggleStatus = async (index) => {
    try {
      const updatedTodos = [...todos];
      updatedTodos[index].status = updatedTodos[index].status === 'Completed' ? 'Pending' : 'Completed';
      setTodos(updatedTodos);
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  return (
    <div className="To-Do">
      <div className='head'>
        <img src='/profile-img.png' className="profile-img" alt="profile-img" />
        <div>
          <h4>Monika Gamage</h4>
          <h6>@monicagamage</h6>
          <Button url="/" style={{ color: '#F4C27F' }}>Log Out</Button>
        </div>
      </div>

      <div className='to-do'>
        <h4>Tasks List</h4>
        <img 
          src='/plus-circle.png' 
          className="plus-img" 
          alt="Add Task" 
          onClick={() => setShowModal(true)} 
          style={{ cursor: "pointer" }}
        />

        <div className="task-container">
          {todos.map((todo, index) => (
            <div className="task-item" key={index}>
              <input 
                type="checkbox" 
                checked={todo.status === 'Completed'}
                onChange={() => toggleStatus(index)}
              />
              <span className={todo.status === 'Completed' ? "completed" : ""}>
                {todo.description}
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Modal for Adding Task */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h4>Add New Task</h4>
            <form onSubmit={postTodos}>
              <input
                type="text"
                className="input"
                placeholder="Task description..."
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">Add</button>
                <button type="button" className="close-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ToDo;
